const contactsMap=document.querySelector(".contacts-map"),modalMap=document.querySelector(".modal-map"),modalCloseMap=document.querySelector(".modal-close-map");contactsMap.addEventListener("click",function(a){a.preventDefault(),modalMap.classList.add("modal-show")}),modalCloseMap.addEventListener("click",function(a){a.preventDefault(),modalMap.classList.remove("modal-show")}),window.addEventListener("keydown",function(a){27===a.keyCode&&modalMap.classList.contains("modal-show")&&(a.preventDefault(),modalMap.classList.remove("modal-show"))});